        com.afrisoftech.lic.GenerateLicence.oAuthKey = oAuthKeyTxt.getText();

        com.afrisoftech.lic.GenerateLicence.passKey = passKeyTxt.getText();

        com.afrisoftech.lic.GenerateLicence.callBackURL = callBackURLTxt.getText();

        this.dispose();

